#
# TABLE STRUCTURE FOR: profile_notices
#

DROP TABLE IF EXISTS profile_notices;

CREATE TABLE `profile_notices` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` varchar(255) COLLATE utf8_bin NOT NULL DEFAULT 'regular' COMMENT 'options: regular, urgent',
  `is_sticky` tinyint(1) NOT NULL,
  `title` text COLLATE utf8_bin NOT NULL,
  `name` varchar(255) COLLATE utf8_bin NOT NULL,
