  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;



#
# TABLE STRUCTURE FOR: queued_emails
#

DROP TABLE IF EXISTS queued_emails;

CREATE TABLE `queued_emails` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `email_data` text COLLATE utf8_bin NOT NULL,
  `priority` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

#
# TABLE STRUCTURE FOR: reports
#

DROP TABLE IF EXISTS reports;

CREATE TABLE `reports` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` tinyint(4) NOT NULL COMMENT '{1: review, 2: profile}',
  `username_or_item_id` int(11) NOT NULL,
  `message` text COLLATE utf8_bin NOT NULL,
  `ip` varchar(255) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `type` (`type`,`username_or_item_id`,`ip`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

#
# TABLE STRUCTURE FOR: requests
#

DROP TABLE IF EXISTS requests;

CREATE TABLE `requests` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `type` tinyint(1) NOT NULL,
  `details` text COLLATE utf8_bin NOT NULL,
  `price` decimal(7,2) NOT NULL,
  `currency` varchar(3) COLLATE utf8_bin NOT NULL,
  `location_name` varchar(255) COLLATE utf8_bin NOT NULL,
  `location_lat` decimal(17,14) NOT NULL,
  `location_lon` decimal(17,14) NOT NULL,
  `location_city` varchar(255) COLLATE utf8_bin NOT NULL,
  `location_country` varchar(255) COLLATE utf8_bin NOT NULL,
  `num_of_applications` int(11) NOT NULL,
  `accepted_id` tinyint(1) NOT NULL,
  `posted` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `status` tinyint(4) NOT NULL COMMENT '{1: open, 2: closed, 3: pending}',
  `shared` tinyint(1) NOT NULL,
  `hidden_on_requests_page` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=706 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;



#
# TABLE STRUCTURE FOR: requests_subjects
#

DROP TABLE IF EXISTS requests_subjects;

CREATE TABLE `requests_subjects` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `request_id` int(11) NOT NULL,
  `subject_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=429 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;



#
# TABLE STRUCTURE FOR: requests_tutors
#

DROP TABLE IF EXISTS requests_tutors;

CREATE TABLE `requests_tutors` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `student_response` text COLLATE utf8_bin NOT NULL,
  `request_id` int(11) NOT NULL,
  `tutor_id` int(11) NOT NULL,
  `message` text COLLATE utf8_bin NOT NULL,
  `price` decimal(7,2) NOT NULL,
  `posted` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `invited` tinyint(1) NOT NULL,
  `status` int(11) NOT NULL,
  `hidden_on_requests_page` tinyint(1) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `request_id` (`request_id`,`tutor_id`),
  UNIQUE KEY `request_id_2` (`request_id`,`tutor_id`),
