) ENGINE=InnoDB AUTO_INCREMENT=113 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;



#
# TABLE STRUCTURE FOR: students_tutors
#

DROP TABLE IF EXISTS students_tutors;

CREATE TABLE `students_tutors` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `student_id` int(11) NOT NULL,
  `tutor_id` int(11) NOT NULL,
  `contacted` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `message` text COLLATE utf8_bin NOT NULL,
