INSERT INTO profile_comments (`id`, `commenter_id`, `commented_user_id`, `parent_id`, `content`, `posted`, `reports`, `deleted`) VALUES (1, 8, 6, 0, 'Artem is very knowledgeable with the bass. He did a bit of a sample before the first lesson, just to show his ability, which was pretty good. He\'s also an incredibly nice guy, and very understanding of whenever I had problems.\n\nHe has a bit of a Russian accent, but it\'s nothing too hard to understand.\n\nIf you want to learn the bass, Artem\'s your guy!', '2013-02-18 06:54:18', 0, 0);
INSERT INTO profile_comments (`id`, `commenter_id`, `commented_user_id`, `parent_id`, `content`, `posted`, `reports`, `deleted`) VALUES (2, 1, 3, 0, 'Muhammad\'s a good teacher. He\'s good at explaining the basics of Hindi. \n\nThe were some more advanced topics (religious words, academic words, etc.) that he didn\'t really know about, but for the basics he was great.', '2013-05-14 08:21:17', 0, 0);
INSERT INTO profile_comments (`id`, `commenter_id`, `commented_user_id`, `parent_id`, `content`, `posted`, `reports`, `deleted`) VALUES (3, 1, 6, 0, 'I meet with Artem once a week for piano practice. I mostly practice on my own, but I like to have someone check up and make sure that everything I\'m doing is proper.\n\nAlso, I speak Russian and all the lessons were in Russian. From what I\'ve heard, his English is very good too.', '2013-10-18 10:47:56', 0, 0);
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (1, 'regular', 1, 'Welcome to Tutorical!', 'make_profile', '<p>Your account is made! <a href=\"{ACCOUNT_PROFILE}\">Click to make your tutor profile</a>.</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (2, 'regular', 0, 'Your profile is made!', 'made_profile', '<p>Check out the <a href=\"{ACCOUNT_MARKETING}\">Marketing page</a> of your account for tips on getting more students. Good luck!</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (3, 'regular', 0, 'Thanks for using Tutorical!', 'thanks', '<p>Just in case you forgot: you rock.</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (5, 'regular', 1, 'One last step: Confirm your email', 'confirm_email', '<p>We sent a link to <a href=\"http://{TUTOR_EMAIL_DOMAIN}\">your email</a>. Just click it and you\'ll be all done! <span class=\"tiny-aftertext\" style=\"display: none;\">(<span class=\"same-page-links no-pointer\" title=\"<ul><li>&raquo; To make sure there aren\'t any typos in your email</li><li>&raquo; To make sure that you have access to the email</li></ul>\">why?</span>)</span></p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (6, 'regular', 1, 'Welcome to Tutorical!', 'welcome_new_student', '<p>You can see all the tutors you\'ve contacted on the <a href=\"{ACCOUNT_TUTORS}\">Tutors</a> page. If you\'d like to change your name or photo, visit the <a href=\"{ACCOUNT_PROFILE}\">Profile</a> page.</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (7, 'regular', 0, 'A potential student has contacted you!', 'new_student_added', '<p>Visit your <a href=\"{ACCOUNT_STUDENTS}\">Students</a> page to accept/deny them.</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (8, 'regular', 0, 'Invited to a tutor request!', 'invited_to_request', '<p>Check your <a href=\"{ACCOUNT_REQUESTS}\">Requests</a> page for more details</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (9, 'regular', 0, 'Rejected from a tutor request', 'rejected_from_request', '<p>Check your <a href=\"{ACCOUNT_REQUESTS}\">Requests</a> page for more details. Remember that you can reapply to the request with a new proposed hourly rate.</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (10, 'regular', 0, 'Accepted to a tutor request!', 'accepted_to_request', '<p>The student has been added to your <a href=\"{ACCOUNT_STUDENTS}\">Students</a> page. Visit it to see their email and to get in touch. Great work!</p>');
INSERT INTO profile_notices (`id`, `type`, `is_sticky`, `title`, `name`, `content`) VALUES (11, 'regular', 1, 'Welcome to Tutorical!', 'admin_import_welcome', '<p>Your profile has been imported. Check out your <a href=\"{ACCOUNT_PROFILE}\">Profile page</a> if you\'d like to add to it. If you\'re done, have a look at the <a href=\"{ACCOUNT_MARKETING}\">Marketing page</a> for tips on getting more students. Good luck!</p>\r\n<br>\r\n<p>Your current password is <b>password</b>. You can change it on your <a href=\"{ACCOUNT_SETTINGS_LINK}\">Settings page</a>.');
