INSERT INTO tutor_profiles (`id`, `user_id`, `gender`, `snippet`, `about`, `average_rating`, `num_of_reviews`, `main_subject_id`, `can_meet_students_home`, `can_meet_tutors_home`, `can_meet_centre`, `can_meet_public`, `can_meet_online_local`, `can_meet_online_distant`, `is_active`, `profile_made`, `shared`) VALUES (11, 13, 'm', 'Great humanities and computers tutor', 'I have worked extensively in one-on-one tutoring in writing, the humanities, and also the social sciences. I hold a highest honors BA from Johns Hopkins University, as well as advanced degrees from Johns Hopkins (MFA, 2010) and McGill (2011). My past students have described me as a \"very knowledgeable\" teacher who \"fostered a positive atmosphere where students felt comfortable discussing concepts and asking questions.\" Hope to hear from you soon!', '0.0', 0, 0, 1, 1, 0, 1, 0, 1, 1, 1, 1);
