INSERT INTO user_experiences (`id`, `user_id`, `company`, `position`, `location`, `start_month`, `end_month`, `start_year`, `end_year`, `description`, `order`) VALUES (169, 307, 'Learning Support Services at Southern University', 'University Tutor', 'Collegedale, TN, USA', 'August', 'May', '2011', '2014', 'I worked for the LSS as a tutor for a variety of subjects, including: General Biology, Genetics, General Microbiology, Cell and Molecular Biology, General Chemistry, Organic Chemistry, Biochemistry, General Physics, ACT preparation, Algebra, Geometry, Trigonometry, and Pre-Calculus. Typically, tutoring sessions were in a one-on-one format and lasted for close to one hour in length.', 1);
