INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (132, 1, '2013-08-08 12:20:34', '2013-08-08 12:20:34', 'To help people just starting out on Tutorical, we\'ve recently added <strong>external reviews</strong>. These are reviews and testimonials of tutors that students have posted on other websites. They are different from Tutorical reviews in a few ways:\r\n<ol class=\"wide-lists\">\r\n	<li><strong>Tutorical reviews can only be posted by Tutorical students</strong> (i.e. students that have contacted a tutor through their Tutorical profile or through a Tutor Request). External reviews are posted on their original website by students, but then copied over by the tutor.</li>\r\n	<li><strong>Tutorical reviews are more detailed than external reviews</strong>, since they include separate ratings for the tutor\'s expertise, knowledge, response time, and helpfulness. They also include an overall rating and a review description. External reviews only require the student\'s name, a link to the original website, and the review description. As such, external reviews can be used for cases where a tutor has testimonials on their website, but no specific \'star\' rating.</li>\r\n	<li><strong>External reviews are linked to another website</strong>. The reason for this is to help the student identify the credibility of the original website, in order to help identify real and fake reviews.</li>\r\n	<li><strong>Only Tutorical reviews affect your average rating</strong>. This helps us deter scammers from making fake 5-star reviews, and moving up in search rankings. External reviews instead make your profile look more appealing, and are a great tool to help tutors new to Tutorical make their profiles stand out.</li>\r\n</ol>\r\nTo make things better, external reviews work seamlessly with the <a title=\"Import your Acadam and UniversityTutor Profiles\" href=\"http://tutorical.com/blog/meta/import-your-acadam-and-universitytutor-profiles\">Tutorical Importer</a>. All reviews imported from a UniversityTutor or Acadam profile are automatically added to your External Reviews.', 'External Reviews', '', 'publish', 'open', 'open', '', 'external-reviews', '', '\nhttp://tutorical.com/blog/meta/import-your-acadam-and-universitytutor-profiles', '2013-09-06 17:06:18', '2013-09-06 17:06:18', '', 0, 'http://tutorical.com/blog/?p=132', 0, 'post', '', 0);
INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (133, 1, '2013-08-08 12:20:34', '2013-08-08 12:20:34', 'To help people just starting out on Tutorical, we\'ve recently added <strong>external reviews</strong>. These are reviews and testimonials of tutors that students have posted on other websites. They are different from Tutorical reviews in a few ways:\r\n<ol>\r\n	<li>Tutorical reviews can only be posted by students that have contacted a tutor through their Tutorical profile or through a Tutor Request. External reviews are posted on their original website by students, but then copied over by the tutor.</li>\r\n	<li>Tutorical reviews are more detailed than external reviews, since they include separate ratings for the tutor\'s expertise, knowledge, response time, and helpfulness. They also include an overall rating and a review description. External reviews only require the student\'s name, a link to the original website, and the review description. As such, external reviews can be used for cases where a tutor has testimonials on their website, but no specific \'star\' rating.</li>\r\n	<li>External reviews are always linked to another website. The reason for this is to help the student identify the credibility of the original website, in order to help identify real and fake reviews.</li>\r\n	<li>Only Tutorical reviews affect your average rating. This helps us deter scammers from making fake 5-star reviews, and moving up in search rankings. External reviews instead make your profile look more appealing, and are a great tool to help tutors new to Tutorical make their profiles stand out.</li>\r\n</ol>\r\nTo make things better, external reviews work seamlessly with the <a title=\"Import your Acadam and UniversityTutor Profiles\" href=\"http://tutorical.com/blog/meta/import-your-acadam-and-universitytutor-profiles\">Tutorical Importer</a>. All reviews imported from a UniversityTutor or Acadam profile are automatically added to your External Reviews.', 'External Reviews', '', 'inherit', 'open', 'open', '', '132-revision-v1', '', '', '2013-08-08 12:20:34', '2013-08-08 12:20:34', '', 132, 'http://tutorical.com/blog/meta/132-revision-v1', 0, 'revision', '', 0);
INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (135, 1, '2013-08-08 12:31:52', '2013-08-08 12:31:52', 'To help people just starting out on Tutorical, we\'ve recently added <strong>external reviews</strong>. These are reviews and testimonials of tutors that students have posted on other websites. They are different from Tutorical reviews in a few ways:\r\n<br><ol>\r\n	<li>Tutorical reviews can only be posted by students that have contacted a tutor through their Tutorical profile or through a Tutor Request. External reviews are posted on their original website by students, but then copied over by the tutor.</li><br>\r\n	<li>Tutorical reviews are more detailed than external reviews, since they include separate ratings for the tutor\'s expertise, knowledge, response time, and helpfulness. They also include an overall rating and a review description. External reviews only require the student\'s name, a link to the original website, and the review description. As such, external reviews can be used for cases where a tutor has testimonials on their website, but no specific \'star\' rating.</li><br>\r\n	<li>External reviews are always linked to another website. The reason for this is to help the student identify the credibility of the original website, in order to help identify real and fake reviews.</li><br>\r\n	<li>Only Tutorical reviews affect your average rating. This helps us deter scammers from making fake 5-star reviews, and moving up in search rankings. External reviews instead make your profile look more appealing, and are a great tool to help tutors new to Tutorical make their profiles stand out.</li>\r\n</ol>\r\n<br>\r\nTo make things better, external reviews work seamlessly with the <a title=\"Import your Acadam and UniversityTutor Profiles\" href=\"http://tutorical.com/blog/meta/import-your-acadam-and-universitytutor-profiles\">Tutorical Importer</a>. All reviews imported from a UniversityTutor or Acadam profile are automatically added to your External Reviews.', 'External Reviews', '', 'inherit', 'open', 'open', '', '132-revision-v1', '', '', '2013-08-08 12:31:52', '2013-08-08 12:31:52', '', 132, 'http://tutorical.com/blog/meta/132-revision-v1', 0, 'revision', '', 0);
