INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (147, 1, '2013-09-08 18:15:16', '2013-09-08 18:15:16', 'Introducing a new feature - <strong>Distance Tutoring</strong>! This refers to tutoring over the phone, via Skype, thorough email, or other means involving long distances.\r\n\r\nIf you\'re a tutor, distance tutoring is a great way to expand your clientele. If you\'re a student, this is an opportunity to choose from a larger pool of tutors. Factor in timezones and you\'ve got tutors available at all hours of the day to help you learn.\r\n<h2>To become a distance tutor...</h2>\r\nJust go to your <a href=\"http://tutorical.com/account/profile\" target=\"_blank\">Edit Profile page</a>  and check off the \"Online (distant)\" box in the \"Can Meet...\" section. Now, when students search for \"Distance Tutors\" teaching one of your subjects, you\'ll pop up.\r\n<h2>Some things to keep in mind...</h2>\r\nFor both students and tutors:\r\n<ul class=\"wide-lists\">\r\n	<li><strong>You\'ll be learning/teaching remotely</strong>. That means through Skype, email, phone, or other means. You probably won\'t be meeting up with each other in-person though</li>\r\n	<li><strong>Means of payment should be discussed in advance</strong>. Since there\'s no in-person meeting, payment will probably be via Paypal, wire transfer, direct deposit, email transfer, or something of the like. Confirm this with one-another advance.</li>\r\n</ul>', 'Distance Tutoring - Tutor and Learn Online', '', 'publish', 'open', 'open', '', 'distance-tutoring-tutor-and-learn-online', '', '', '2013-09-08 18:18:46', '2013-09-08 18:18:46', '', 0, 'http://tutorical.com/blog/?p=147', 0, 'post', '', 0);
