INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (153, 1, '2013-09-08 23:08:09', '2013-09-08 23:08:09', 'Finding local tutors has just gotten easier with Tutorical\'s new <strong>Subjects Search</strong>. Now you can select a location and find all the subjects currently being taught there. When you see the subject you\'re interested in, click it and you\'ll see all the tutors that teach it.\r\n\r\nIf you\'re looking for a distance tutor, just select \"<strong>Distance Subjects</strong>\" from the search bar and you\'ll see a list of all subjects currently being taught online.', 'Subjects Search - Find all subjects being taught near you', '', 'inherit', 'open', 'open', '', '152-revision-v1', '', '', '2013-09-08 23:08:09', '2013-09-08 23:08:09', '', 152, 'http://tutorical.com/blog/meta/152-revision-v1', 0, 'revision', '', 0);
INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (154, 1, '2013-09-13 00:55:15', '2013-09-13 00:55:15', 'This blog is our connection to students and tutors using Tutorical.\r\n\r\nHere are a few things we cover:\r\n<h3>Student Resources</h3>\r\nThis includes studying tips and tools, posts about learning techniques, as well as reviews of software and websites teaching various subjects.\r\n<h3>Tutor Resources</h3>\r\nThis includes teaching tips,  links to programs and websites related to tutoring, and resources that could help you tutor and manage your students.\r\n<h3>Meta</h3>\r\nThis section is for both students and tutors. For students, it covers how to use Tutorical to help find the perfect tutor. For tutors, it covers how to make the most of their Tutorical profile, as well as tips that will make using Tutorical even easier. Also, new feature announcements appear in this category.', 'Welcome to the Tutorical Blog', 'A little intro to the blog - who should read it, what kind of posts to expect, etc.', 'inherit', 'open', 'open', '', '44-revision-v1', '', '', '2013-09-13 00:55:15', '2013-09-13 00:55:15', '', 44, 'http://tutorical.com/blog/meta/44-revision-v1', 0, 'revision', '', 0);
INSERT INTO wp_posts (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (156, 1, '2013-09-18 03:08:54', '2013-09-18 03:08:54', 'We\'ve recently added the \'type\' option to tutor requests, which allows you to specify what type of tutors should apply to your request.\r\n\r\nOptions include:\r\n<ul>\r\n	<li><strong>Local</strong> - Tutors near you</li>\r\n	<li><strong>Distance</strong> - Tutors anywhere in the world (see <a title=\"Distance Tutoring – Tutor and Learn Online\" href=\"http://tutorical.com/blog/distance-tutoring-tutor-and-learn-online/\">Distance Tutoring</a>)</li>\r\n	<li><strong>Both</strong> - Local and distance tutors</li>\r\n</ul>\r\nIf a request\'s type is \'Distance\' or \'Both\', it\'ll show up in \'Distance Request\' searches. Likewise if the type is \'Local\' or \'Both\', it\'ll show up in \'Local Request\' searches.', 'Distance Tutor Requests', '', 'publish', 'open', 'open', '', 'distance-tutor-requests', '', '', '2013-09-18 03:08:54', '2013-09-18 03:08:54', '', 0, 'http://tutorical.com/blog/?p=156', 0, 'post', '', 0);
